mapper.update:

update article
set a_modidate = sysdate,a_title=#{a_title},a_content=#{a_content}
where a_no=#{a_no}


update article
set a_isshow='N'
where a_no=#{a_no}


update article_comment
set ac_modidate = sysdate, ac_content=#{ac_content}
where ac_no=#{ac_no}


update article_comment
set ac_isshow='N'
where ac_no=#{ac_no}


update article
set a_cnt=a_cnt +1
where a_no=#{a_no}


update article
set a_cnt=a_cnt -1
where a_no=#{a_no}
